<html>
	<head>
		<meta charset=\"UTF-8\">
		<script src=\"https://ajax.googleapis.com/ajax/libs/jquery/3.1.1/jquery.min.js\"></script>

		<style>
			.button {
    display:inline-block;
    padding:50px 80px;
    background-color:#69c;
    color:#fff;
    border:1px solid #69c;
    text-decoration:none;
    font-size:13px;
    -webkit-border-radius:3px;
    -moz-border-radius:3px;
    border-radius:3px;
}
.button:hover{
    background-color:#79acdf;
    -webkit-box-shadow:inset 0px 0px 3px #fff;
    -moz-box-shadow:inset 0px 0px 3px #fff;
    box-shadow:inset 0px 0px 3px #fff;
}
.button:active{
    padding-top:6px;
    padding-bottom:4px;
    border:1px solid #334c66;
    background-color:#69c;
    color:#e0ebf5;
    -webkit-box-shadow:inset 0px 0px 8px #334c66;
    -moz-box-shadow:inset 0px 0px 2px #3a6da0;
    box-shadow:inset 0px 0px 2px #3a6da0;
}
			.key {
				height : 10%;
				width : 95%;
				position : absolute;
				bottom : 10%;
				background : #FFFBBC;
			}
			.noad {
				width: 15px;
				height: 15px;
				background: #C6FFBF;
			}
		</style>





	</head>
	<body>

		<div class=\'noad\'>
			・
		</div>

		<div class=\'key'>
			<button id=\'up\' class=\"button\">⇧</button>
			<button id=\'down\' class=\"button\">⇩</button>
			<button id=\'right\' class=\"button\">⇦</button>
			<button id=\'left\' class=\"button\">⇨</button>
		</div>

		<script>
			var down = 0;
			var up = 0;
			var left = 0;
			var right = 0;
			$(document).click(function(event){
				var target = $(event.target);
				if(target.attr(\'id\') == \'down\'){
					down++;
					$(\'.noad\').css(\'padding-top\',(down * 30) + \'px\');
				}
				if(target.attr(\'id\') == \'up\'){
					up++;
					$(\'.noad\').css(\'padding-top\',(up * -30) + \'px\');
				}
				if(target.attr(\'id\') == \'left\'){
					left++;
					$(\'.noad\').css(\'padding-left\',(left * 30) + \'px\');
				}
				if(target.attr(\'id\') == \'right\'){
					right++;
					$(\'.noad\').css(\'padding-left\',(right * -30) + \'px\');
				}
		});
		</script>
	</body>
</html>
